package Operator;

public class Unary {

	public static void main(String[] args) {
		int a=7;
		System.out.println("a=:"+ a++);//a=a+1//
		System.out.println("a=:"+ a);//a//
		
		int b=8;
		System.out.println("b=:"+ b--);
		System.out.println("b=:"+ b);
		
		int c=9;
		System.out.println("c=:"+ ++c);
		
		int q=11;
		System.out.println("q=:"+ --q);
		
		
		// TODO Auto-generated method stub

	}

}
